import React from 'react';
import { useLanguage } from '../contexts/LanguageContext';

const Header = ({ user, onLogout }) => {
  const { language, toggleLanguage, t } = useLanguage();

  return (
    <header className="header">
      <div className="header-content">
        <div className="logo">
          <img src="/suraksha-logo.svg" alt="Suraksha Logo" className="header-logo" />
          <span>{t('brand_name')}</span>
        </div>
        <div className="user-info">
          <button 
            onClick={toggleLanguage}
            className="language-toggle"
            title={`Switch to ${language === 'hi' ? 'English' : '‡§π‡§ø‡§Ç‡§¶‡•Ä'}`}
          >
            {language === 'hi' ? 'EN' : '‡§π‡§ø‡§Ç'}
          </button>
          <span>{t('welcome')}, <strong>{user.name}</strong></span>
          <span className="role-badge">
            {user.role === 'admin' ? `üëë ${t('admin')}` : `üë®‚Äç‚öïÔ∏è ${t('medical_professional')}`}
          </span>
          <button className="logout-btn" onClick={onLogout}>
            üö™ {t('logout')}
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
