import React, { useState } from 'react';
import { authAPI } from '../api';

const Login = ({ onLogin }) => {
  const [activeRole, setActiveRole] = useState('admin');
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await authAPI.login({
        ...formData,
        role: activeRole,
      });

      if (response.data.success) {
        onLogin(response.data.user);
      }
    } catch (error) {
      setError(error.response?.data?.error || 'Login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <img src="/suraksha-logo.svg" alt="Suraksha Logo" className="login-logo" />
          <h1 className="login-title">Suraksha Medical Training</h1>
          <p className="login-subtitle">Secure Healthcare Training Management</p>
        </div>
        
        <div className="role-tabs">
          <button
            className={`role-tab ${activeRole === 'admin' ? 'active' : ''}`}
            onClick={() => setActiveRole('admin')}
          >
            Administrator
          </button>
          <button
            className={`role-tab ${activeRole === 'professional' ? 'active' : ''}`}
            onClick={() => setActiveRole('professional')}
          >
            Medical Professional
          </button>
        </div>

        {error && (
          <div className="alert alert-error fade-in">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">Username</label>
            <input
              type="text"
              name="username"
              className="form-input"
              value={formData.username}
              onChange={handleInputChange}
              required
              placeholder="Enter your username"
            />
          </div>

          <div className="form-group">
            <label className="form-label">Password</label>
            <input
              type="password"
              name="password"
              className="form-input"
              value={formData.password}
              onChange={handleInputChange}
              required
              placeholder="Enter your password"
            />
          </div>

          <button
            type="submit"
            className="btn btn-primary btn-full"
            disabled={loading}
          >
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>

        <div className="demo-credentials">
          <strong>ðŸ”‘ Demo Credentials:</strong>
          <br />
          <strong>Admin:</strong> Username: "admin", Password: "admin123"
          <br />
          <strong>Professional:</strong> Username: "drsmith", Password: "9876543210"
        </div>
      </div>
    </div>
  );
};

export default Login;
